import process from 'node:process'
import dotenv from 'dotenv'
dotenv.config({ debug: true })

const defaults = {
  client:   'mysql2',
  host:     'localhost',
  port :    3306,
  pool_min: 2,
  pool_max: 10,
}

export const client = process.env.DATABASE_CLIENT || defaults.client;

export const connection = process.env.DATABASE_CONNECTION || {
  host:     process.env.DATABASE_HOST || defaults.host,
  port:     process.env.DATABASE_PORT || defaults.port,
  database: process.env.DATABASE_NAME,
  user:     process.env.DATABASE_USER,
  password: process.env.DATABASE_PASSWORD,
  // filename: process.env.DATABASE_FILENAME,
  // flags:    splitList(process.env.DATABASE_FLAGS),
};

export const pool = {
  min: process.env.DB_POOL_MIN || defaults.pool_min,
  max: process.env.DB_POOL_MAX || defaults.pool_max,
};

export const Config = { client, connection, pool };

export default Config;

import Config from './Config.js'
import Knex from 'knex'

export class Connection {
  constructor(config=Config) {
    this.config = config;
    this.knex   = Knex(config);
  }
  query() {
    // most queries should be handled by Knex instance
    return this.knex(...arguments);
  }
  raw() {
    return this.knex.raw(...arguments);
  }
  acquire() {
    // acquire a connection from the knex client pool in
    // case we need to go direct to the database client
    return this.knex.client.pool.acquire();
  }
  pool() {
    return this.knex.client.pool;
  }
  destroy() {
    return this.knex.destroy();
  }
}

export const connection = config => new Connection(config)

export default Connection

import { fail } from '@abw/badger-utils';
import Config from './Config.js'
import Connection from './Connection.js'
import Table from './Table.js';

const escapeChars = {
  mysql:  '`',
  mysql2: '`',
  default: '"',
};

class Database {
  constructor(params={ }) {
    const config    = { ...Config, ...params };
    this.connection = new Connection(config);
    this.tables     = config.tables || { };
    this.escapeChar = escapeChars[config.client||'default'] || escapeChars.default;
    this.state      = {
      table: { },
    };
  }
  query() {
    return this.connection.query(...arguments);
  }
  raw() {
    return this.connection.raw(...arguments);
  }
  table(name) {
    return this.state.table[name]
      ||  (this.state.table[name] = this.initTable(name));
  }
  initTable(name) {
    const schema = this.tables[name] || fail("Invalid table specified: " + name);
    schema.table ||= name;
    const tclass = schema.tableClass || Table;
    return new tclass(this, schema);
  }
  escape(name) {
    return name
      .split(/\./)
      .map( part => this.escapeChar + part + this.escapeChar)
      .join('.');
  }
  destroy() {
    return this.connection.destroy();
  }
}

export const database = config => new Database(config)

export default Database

