    return this.factory(this, 'from', table);
  }
  where(condition) {
    this.debug("+where")
    return this.factory(this, 'where', condition);
  }
  dumpChain() {
    return this.parent
      ? this.parent.dumpChain() + "\n" + this.dump()
      : '';
  }
}

