export const recordProxy = record =>
  new Proxy(
    record,
    {
      get(target, prop) {
        if (target.schema.allColumns[prop]) {
          return target.data[prop];
        }
        return target[prop];
      }
    }
  );

export default recordProxy

export const rowProxy = table => query =>
  new Proxy(
    query,
    {
      get(target, prop) {
        if (prop === 'record') {
          return () => table.record(...arguments);
        }
        else if (prop === 'then') {
          return (
            fn => table.rowProxy(target.then(fn))
          ).bind(target)
        }
        return target[prop];
      }
    }
  );

export default rowProxy

export const rowsProxy = table => query =>
  new Proxy(
    query,
    {
      get(target, prop) {
        if (prop === 'records') {
          return () => table.records(...arguments);
        }
        else if (prop === 'then') {
          return (
            fn => table.rowsProxy(target.then(fn))
          ).bind(target)
        }
        return target[prop];
      }
    }
  );

export default rowsProxy

