import { hasValue, isString, splitList, fail, objMap, isArray } from "@abw/badger-utils";
import { splitHash } from "./Utils.js";
import { addDebug } from "@abw/badger";

const DEFAULT_ID = 'id';
const COLUMN_SET_PREFIX = /^(@|\.\.\.)/;

export class Schema {
  constructor(database, schema) {
    this.database       = database || fail("No database specified");
    this.table          = schema.table || fail("No table name specified");

    this.prepareColumns(schema);
    this.prepareKeys(schema);
    this.prepareColumnSets(schema);

    this.virtualColumns = schema.virtualColumns || { };
    //this.tableColumns   = prepareColumns(this.columnNames, this.table);
    //this.columnSets     = prepareColumnSets(this.columnNames, schema.columnSets);
    this.allColumns     = { ...splitHash(this.columnNames), ...splitHash(Object.keys(this.virtualColumns)) };

    //console.log('columnNames: ', this.columnNames);
    //console.log('virtualColumns: ', this.virtualColumns);
    //console.log('allColumns: ', this.allColumns);
    /// this.allKeys = splitHash(this.keys);

    // TODO: column sets for select, update, etc.

    addDebug(this, schema.debug, schema.debugPrefix || 'Schema', schema.debugColor);
  }
  prepareColumns(schema) {
    const table      = schema.table;
    const columns    = schema.columns;
    const index      = splitHash(columns, () => ({ }))
    this.columnIndex = objMap(
      index,
      (value, key) => {
        // column name can be defined in column spec as 'column' in case
        // the database column name doesn't match the name you want to use
        const column = value.column || (value.column = key);
        // tableColumn is the full "table.column"
        value.tableColumn = `${table}.${column}`;
        return value;
      }
    )
    this.columnNames  = Object.keys(this.columnIndex);
    this.tableColumns = objMap(
      this.columnIndex,
      value => value.tableColumn
    );
  }
  prepareColumnSets(schema) {
    const index = splitHash(schema.columnSets, () => ({ }))
    this.columnSets = objMap(
      index,
      (spec) => {
        const basis   = (isString(spec) || isArray(spec)) ? splitList(spec) : this.columnNames;
        const include = splitList(spec.include);
        const exclude = splitHash(spec.exclude);
        const result = [...basis, ...include]
          .filter( column => ! exclude[column] );
        // console.log('spec: ', spec, ' => ', result);
        // console.log('basis: ', basis, '   exclude: ', exclude);
        return result;
      }
    )
    //console.log('mapped columnSets: ', this.columnSets);
  }
  prepareKeys(schema) {
    this.keys = splitList(schema.keys);
    if (schema.id) {
      this.id = schema.id;
      this.keys.unshift(this.id);
    }
    else if (this.keys.length === 0) {
      this.id = DEFAULT_ID;
      // console.log('setting default id: ', this.id);
      this.keys.unshift(this.id);
    }
    this.keyIndex = splitHash(this.keys);
  }

  // TODO: refactor remaining methods
  column(name) {
    return this.tableColumns[name]
      ? this.tableColumns[name]
      : this.virtualColumns[name]
        ? this.database.raw(`${this.virtualColumns[name]} as ${name}`)
        : fail('Invalid column specified: ', name);
  }
  columnSet(name) {
    return this.columnSets[name]
      || fail('Invalid columnSet specified: ', name);
  }
  defaultColumns() {
    return this.columnSets.default
      || this.columns;
  }
  columns(names) {
    return hasValue(names)
      ? this.resolveColumns(names)
      : this.defaultColumns().map( name => this.column(name) );
  }
  resolveColumns(names) {
    const list = splitList(names);
    let cols = [ ];
    list.forEach(
      name => {
        this.debug("resolveColumns(%s) / %s", names, name);
        if (isString(name)) {
          if (name.match(COLUMN_SET_PREFIX)) {
            this.debug("%s is a columnSet %s", name, name.replace(COLUMN_SET_PREFIX, ''));
            cols.push(
              ...this.columnSet(name.replace(COLUMN_SET_PREFIX, ''))
                .map( name => this.column(name) )
            );
          }
          else {
            this.debug("%s is a column", name);
            cols.push(this.column(name));
          }
        }
        else {
          fail("resolveColumns() can only currently resolve strings");
        }
      }
    )
    this.debug("resolveColumns(%s) => ", names, cols)
    return cols;
  }
  identity(data) {
    return this.keys.reduce(
      (result, key) => {
        result[key] = data[key]
        return result
      },
      {}
    );
  }
  separateKeyColumns(data) {
    let keys    = { };
    let columns = { };
    let rejects = { };
    Object.entries(data).forEach(
      ([key, value]) => {
        if (this.allKeys[key]) {
          keys[key] = value;
        }
        else if (this.allColumns[key]) {
          columns[key] = value;
        }
        else {
          rejects[key] = value;
        }
      }
    )
    return { keys, columns, rejects };
  }
}

export const schema = (database, schema) =>
  new Schema(database, schema);

export default Schema

import { addDebug } from "@abw/badger";
import { fail, isArray } from "@abw/badger-utils";
import recordProxy from "./Proxy/Record.js";
import rowProxy from "./Proxy/Row.js";
import rowsProxy from "./Proxy/Rows.js";
import Record from "./Record.js";
import Schema from "./Schema.js";
// import Queries from "./Queries.js";

// export class Table extends Queries {
export class Table {
  constructor(database, schema) {
    this.database    = database || fail("No database specified");
    this.schema      = new Schema(database, schema)
    this.recordClass = schema.recordClass || Record;
    this.rowProxy    = rowProxy(this);
    this.rowsProxy   = rowsProxy(this);

    addDebug(this, schema.debug, schema.debugPrefix || `${this.table} table`, schema.debugColor);
  }
  query() {
    return this.database.query(this.schema.table);
  }
  insert(data) {
    return isArray(data)
      ? this.insertRows(data)
      : this.insertRow(data);
  }
  insertRow(row) {
    return this.rowProxy(
      this.query().insert(row).then(
        ([id]) => this.query().select().first().where({ [this.schema.id]: id })
      )
    )
  }
  insertRows(rows) {
    return this.rowsProxy(
      this.insertRowsAsync(rows)
    )
    /*
    // I *think* this should work... but it's beyond my ability/patience to
    // figure out why it doesn't :-(
    /*
    return this.rowsProxy(
      Promise.all(
        rows.map(
          data => this.query().insert(data)
            .then(
              ([id]) => this.query().select().first().where({ [this.schema.id]: id })
            )
        )
      )
    )
    */
  }
  async insertRowsAsync(rows) {
    let results = [ ];
    for (const row of rows) {
      const result = await this.query().insert(row)
        .then(
          ([id]) => this.query().select().first().where({ [this.schema.id]: id })
        )
      results.push(result)
    }
    return results;
  }
  selectAll(columns) {
    return this.rowsProxy(
      this.query().select(
        this.schema.columns(columns)
      )
    );
  }
  selectOne(columns) {
    return this.rowProxy(
      this.query().select(
        this.schema.columns(columns)
      )
    ).first();
  }
  fetchAll(where) {
    const select = this.query().select();
    return this.rowsProxy(
      where
        ? select.where(where)
        : select
    );
  }
  fetchOne(where) {
    const select = this.query().select().first();
    return this.rowProxy(
      where
        ? select.where(where)
        : select
    );
  }
  update(set, where) {
    return this.rowsProxy(
      this.updateAsync(set, where)
    )
  }
  async updateAsync(set, where) {
    return await this.query().update(set).where(where).then(
      () => this.fetchAll(where)
    )
  }
  record(query) {
    return query.then(
      row => recordProxy(new this.recordClass(this, row))
    );
  }
  records(query) {
    // console.log('table.records()');
    return query.then(
      rows => rows.map( row => recordProxy(new this.recordClass(this, row)) )
    );
  }
}

export const table = (database, schema) => new Table(database, schema)

export default Table;

import { isFunction, isObject, splitList } from '@abw/badger-utils'

export function splitHash(string, value=true, hash={ }) {
  // if it's already a hash object then return it unchanged
  if (isObject(string)) {
    return string;
  }
  // split a string into an array (or leave an array unchanged)
  const items = splitList(string);
  // populate the hash with the default value
  items.forEach(
    i => hash[i] = isFunction(value)
      ? value(i)
      : value
  );
  return hash;
}

# Table

This is a wrapper around a database table.

Conceptually we think of a table as being a collection of records.  I prefer to use
plural names (e.g. `users` and `companies` instead of `user` and `company`) in
keeping with that paradigm, but you can use any names you like.

The names of the tables you define for your application don't have to match the names
of the underlying database table.

## Configuration

Tables are defined via the `tables` item in a database configuration.

```js
import Database from '@abw/badger-database'

const database = new Database(
  // ...client, connection, pool, etc...
  tables: {
    users: {
      // ... schema for users table
    },
    companies: {
      // ... schema for companies table
    }
  }
)
```

You can then access a table object using the `table()` method.

```js
const users = database.table('users');
```

The configuration items for a table are as follows.

### table

The default behaviour is to assume that the name of the underlying database table
is the same as the collection name.  If it isn't then you can use the `table`
configuration option to define it.

For example, if you want to define a collection of `users` in your application but the
underlying table name is `user` then you can define it like so:

```js
const database = new Database(
  // ...client, connection, pool, etc...
  tables: {
    users: {
      table: 'user'
    },
  }
)
```

### columns

The `columns` configuration item is used to define the columns that you want the
collection to have access to.  You don't have to include all of the columns in the
database table if you don't want to.

In the simplest case you can define the columns as a string of whitespace delimited
names.

```js
const database = new Database(
  // ...client, connection, pool, etc...
  tables: {
    users: {
      columns: 'id name email'
    },
  }
)
```

This is shorthand for specifying them as an array, which you can do if you prefer
or already have the column names in an array.

```js
const database = new Database(
  // ...client, connection, pool, etc...
  tables: {
    users: {
      columns: ['id', 'name', 'email']
    },
  }
)
```

The third option is to use a hash object to define the columns.  This allows you to
provide additional metadata about the columns.  You can add any metadata you like.
At the time of writing the metadata isn't used internally, but it might be soon.

```js
const database = new Database(
  // ...client, connection, pool, etc...
  tables: {
    users: {
      columns: {
        id: {
          type: 'id',
          automatic: true,
        },
        name: {
          type: 'text',
          required: true,
        },
        email: {
          type: 'text',
          required: true,
        }
      }
    },
  }
)
```

